import React, { useState, useEffect } from "react"
import { Route, NavLink, Switch } from "react-router-dom";
import facade from "./loginFacade";
import flightFacade from "./flightFacade";
import './NavbarStyle.css';

const Header = (props) => {
  return (
    <ul className="header">
      <li><NavLink exact activeClassName="active" to="/">Startpage</NavLink></li>
      <li><NavLink activeClassName="active" to="/searchpage">Search for flight</NavLink></li>
      <li><NavLink activeClassName="active" to="/seeallpage">See all flights</NavLink></li>
      <li><NavLink activeClassName="active" to="/myprofilepage">My profile</NavLink></li>
      {!props.loggedIn ? 
      (<li><NavLink activeClassName="active" to="/loginpage">Log in</NavLink></li>) : 
      (<li><button onClick={props.logout}>Log out</button></li>)}
    </ul>
  );
};

const Home = () => {
  return (
    <div>
      <h1>Welcome to Just Travel</h1>
      <p>You can search for a flight or see all flights - just hit the navigationbar!</p>
    </div>
  );
};

function LogIn(props) {
  const init = { username: "", password: "" };
  const [loginCredentials, setLoginCredentials] = useState(init);

  const performLogin = (evt) => {
    evt.preventDefault();
    props.login(loginCredentials.username, loginCredentials.password);
  }

  const onChange = (evt) => {
    setLoginCredentials({ ...loginCredentials, [evt.target.id]: evt.target.value })
  }

  return (
    <div>
      <h2>Login</h2>
      <form onChange={onChange} >
        <input placeholder="Username" id="username" />
        <input placeholder="Password" id="password" />
        <button onClick={performLogin}>Login</button>
      </form>
    </div>
  )
}

const NoMatch = () => {
  return (
    <h1>This page does not exist!</h1>
  );
};

const getFlights = () => {
  const init = [{
    flightId: "loading",
    departure: "loading",
    arrival: "loading",
    destinationAirport: "loading",
    takeoffAirport: "loading",
    price: "loading"


  }];

  const [flightData, setFlightData] = useState(init);

  useEffect(() => {
    computerFacade.getAllComputers().then(data => setComputerData(data));
    flightFacade.getFlights().then(data => setFlightData(data));
  }, []);

  return (
    <div>
      <br></br>
      <h2>List of all flights</h2>
      <table>
        <thead>
          <tr>
          {Object.keys(flightData[0]).map(header => <th key={header}>{header}</th>)}
          </tr>
        </thead>
        <tbody>
          {flightData.map((flight, index) => <tr key={index}><td>{}</td><td>{flight.flightId}</td><td>{flight.departure}</td><td>{flight.arrival}</td><td>{flight.destinationAirport}</td><td>{flight.takeoffAirport}</td><td>{flight.price}</td></tr>)}
        </tbody>
      </table>
    </div>
  );
};



function App() {
  const init = facade.loggedIn;
  const [loggedIn, setLoggedIn] = useState(init)

  const logout = () => {
    facade.logout()
    setLoggedIn(false)
  }
  const login = (user, pass) => {
    facade.login(user, pass)
      .then(res => setLoggedIn(true));
  }

  return (
    <div>
      <Header loggedIn={loggedIn} logout={logout}/>
      <Switch>
        <Route exact path="/">
          <Home/>
        </Route>
        <Route path="/searchpage">

        </Route>
        <Route path="/seeallpage">
        <getFlights/>
        </Route>
        <Route path="/myprofilepage">

        </Route>
        <Route path="/loginpage">
          <LogIn/>
        </Route>
        <Route>
          <NoMatch />
        </Route>
      </Switch>
    </div>
  )
}
export default App;
