import React, { useState, useEffect } from "react"
import { Route, NavLink, Switch } from "react-router-dom";
import facade from "./loginFacade";
import demoFacade from "./demoFacade";
import './NavbarStyle.css';

const Header = () => {
  return (
    <ul className="header">
      <li><NavLink exact activeClassName="active" to="/">Home</NavLink></li>
      <li><NavLink activeClassName="active" to="/userpage">User page</NavLink></li>
      <li><NavLink activeClassName="active" to="/adminpage">Admin page</NavLink></li>
      <li><NavLink activeClassName="active" to="/datapage">Data page</NavLink></li>
      <li><NavLink activeClassName="active" to="/welcomepage">Welcome page</NavLink></li>
    </ul>
  );
};

const Home = () => {
  const [loggedIn, setLoggedIn] = useState(false)

  const logout = () => {
    facade.logout()
    setLoggedIn(false)
  }
  const login = (user, pass) => {
    facade.login(user, pass)
      .then(res => setLoggedIn(true));
  }
  return (
    <div>
      <h1>Hello! Just testing home!</h1>
      {!loggedIn ? (<LogIn login={login} />) : (<LoggedIn logout={logout} />)}
    </div>
  );
};
const User = () => {
  const data = demoFacade.getUser();
  return (
    <h1>Hello! Just testing user! - {data.msg}</h1>
  );
};
const Admin = () => {
  return (
    <h1>Hello! Just testing admin!</h1>
  );
};

const Data = () => {
  return (
    <h1>Hello! Just testing data!</h1>
  );
};

const Welcome = () => {
  return (
    <h1>Hello! Just testing welcome!</h1>
  );
};

const NoMatch = () => {
  return (
    <h1>Hello! Just testing nomatch!</h1>
  );
};

function LogIn({ login }) {
  const init = { username: "", password: "" };
  const [loginCredentials, setLoginCredentials] = useState(init);

  const performLogin = (evt) => {
    evt.preventDefault();
    login(loginCredentials.username, loginCredentials.password);
  }
  const onChange = (evt) => {
    setLoginCredentials({ ...loginCredentials, [evt.target.id]: evt.target.value })
  }

  return (
    <div>
      <h2>Login</h2>
      <form onChange={onChange} >
        <input placeholder="User Name" id="username" />
        <input placeholder="Password" id="password" />
        <button onClick={performLogin}>Login</button>
      </form>
    </div>
  )

}
function LoggedIn(props) {
  const [dataFromServer, setDataFromServer] = useState("Loading...")

  useEffect(() => { facade.fetchData().then(data => setDataFromServer(data.msg)); }, [])

  return (
    <div>
      <h2>Data Received from server</h2>
      <h3>{dataFromServer}</h3>
      <button onClick={props.logout}>Logout</button>
    </div>
  )

}

function App() {


  return (
    <div>
      <Header />
      <Switch>
        <Route exact path="/">
          <Home />
        </Route>
        <Route path="/userpage">
          <User />
        </Route>
        <Route path="/adminpage">
          <Admin />
        </Route>
        <Route path="/datapage">
          <Data />
        </Route>
        <Route path="/welcomepage">
          <Welcome />
        </Route>
        <Route>
          <NoMatch />
        </Route>
      </Switch>
    </div>

  )

}
export default App;
